import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

import Login from './components/Login';
import Register from './components/Register';
import CreateCommandeModal from './components/CreateCommandeModal';
import EditCommandeModal from './components/EditCommandeModal';
import ConfirmModal from './components/ConfirmModal';

function App() {
  // States
  const [commandes, setCommandes] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null); // 'admin' or 'user'
  const [showRegister, setShowRegister] = useState(false);

  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingCommande, setEditingCommande] = useState(null);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // On app load, check token & role
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserRole(decoded.role);
        setIsLoggedIn(true);
      } catch {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
      }
    }
  }, []);

  // Fetch commandes when logged in
  useEffect(() => {
    if (isLoggedIn) fetchCommandes();
  }, [isLoggedIn]);

  // Reset page on search change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm]);

  // Fetch commandes from middleware API
  const fetchCommandes = async () => {
    setLoading(true);
    const token = localStorage.getItem('token');
    if (!token) {
      setError('Non authentifi√©');
      setCommandes([]);
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }
    try {
      const response = await fetch('http://localhost:4000/list-commandes', {
        headers: { Authorization: 'Bearer ' + token },
      });
      if (!response.ok) throw new Error('Erreur API: ' + response.status);
      const data = await response.json();
      setCommandes(data.data || []);
      setError(null);
    } catch (err) {
      setError(err.message);
      setCommandes([]);
      if (err.message.includes('401') || err.message.includes('403')) {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        setUserRole(null);
      }
    } finally {
      setLoading(false);
    }
  };

  // Filter commandes
  const filteredCommandes = commandes.filter(cmd => {
    const term = searchTerm.toLowerCase();
    return (
      cmd.source.toLowerCase().includes(term) ||
      (cmd.contenu.pizza && cmd.contenu.pizza.toLowerCase().includes(term)) ||
      cmd.statut.toLowerCase().includes(term)
    );
  });

  // Pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentCommandes = filteredCommandes.slice(indexOfFirstItem, indexOfLastItem);

  // Logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUserRole(null);
    setCommandes([]);
    setError(null);
    setCurrentPage(1);
  };

  // Delete commandes
  const askDelete = id => setConfirmDeleteId(id);
  const cancelDelete = () => setConfirmDeleteId(null);
  const confirmDelete = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:4000/delete-commande/${confirmDeleteId}`, {
        method: 'DELETE',
        headers: { Authorization: 'Bearer ' + token },
      });
      if (!response.ok) throw new Error('Erreur suppression');
      setConfirmDeleteId(null);
      fetchCommandes();
    } catch (error) {
      alert('Erreur lors de la suppression : ' + error.message);
    }
  };

  // Edit commandes
  const startEditing = cmd => setEditingCommande(cmd);
  const cancelEditing = () => setEditingCommande(null);
  const saveEditing = async updated => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:4000/update-commande/${updated.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: 'Bearer ' + token,
        },
        body: JSON.stringify(updated),
      });
      const data = await response.json();
      if (data.code === '00000') {
        alert('Commande modifi√©e');
        setEditingCommande(null);
        fetchCommandes();
      } else {
        alert('Erreur modification : ' + data.message);
      }
    } catch {
      alert('Erreur de connexion');
    }
  };

  // Show Login/Register if not logged in
  if (!isLoggedIn) {
    if (showRegister) {
      return <Register onRegister={() => setShowRegister(false)} />;
    }
    return <Login onLogin={() => { setIsLoggedIn(true); fetchCommandes(); }} onShowRegister={() => setShowRegister(true)} />;
  }

  return (
    <div className="container mt-4">
      <h1 className="mb-4">üßæ Liste des Commandes</h1>

      <div className="d-flex justify-content-between align-items-center mb-3">
        <div>
          <button className="btn btn-danger" onClick={handleLogout}>
            üîì D√©connexion
          </button>
        </div>
        <div>
          <span>R√¥le connect√© : <strong>{userRole}</strong></span>
        </div>
        <div>
          <button
            className="btn btn-info"
            onClick={() => window.open('http://localhost:3001', '_blank')}
          >
            Ouvrir Pizza Demo Frontend
          </button>
        </div>
      </div>

      {/* Only admin can create commandes */}
      {userRole === 'admin' && (
        <button className="btn btn-success mb-3" onClick={() => setShowCreateModal(true)}>
          ‚ûï Cr√©er une commande
        </button>
      )}

      <CreateCommandeModal
        show={showCreateModal}
        handleClose={() => setShowCreateModal(false)}
        onCreate={fetchCommandes}
      />

      <button className="btn btn-primary mb-3" onClick={fetchCommandes}>üîÑ Recharger la liste</button>

      {error && <p className="text-danger">Erreur : {error}</p>}

      <ConfirmModal
        isOpen={confirmDeleteId !== null}
        message="Voulez-vous vraiment supprimer cette commande ?"
        onConfirm={confirmDelete}
        onCancel={cancelDelete}
      />

      {editingCommande && (
        <EditCommandeModal
          commande={editingCommande}
          onClose={cancelEditing}
          onSave={saveEditing}
        />
      )}

      {loading && (
        <div className="text-center my-3">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      )}

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Rechercher par source, pizza ou statut..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
      />

      <table className="table table-striped table-bordered">
        <thead className="table-light">
          <tr>
            <th>ID</th>
            <th>Source</th>
            <th>Contenu</th>
            <th>Statut</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentCommandes.map(cmd => (
            <tr key={cmd.id}>
              <td>{cmd.id}</td>
              <td>{cmd.source}</td>
              <td>
                <div>
                  <div>Pizza : {cmd.contenu.pizza || 'N/A'}</div>
                  <div>Quantit√© : {cmd.contenu.quantity || 'N/A'}</div>
                </div>
              </td>
              <td>{cmd.statut}</td>
              <td>{new Date(cmd.date_commande).toLocaleString()}</td>
              <td>
                {userRole === 'admin' && (
                  <>
                    <button className="btn btn-sm btn-warning me-2" onClick={() => startEditing(cmd)}>‚úèÔ∏è Modifier</button>
                    <button className="btn btn-sm btn-danger" onClick={() => askDelete(cmd.id)}>üóëÔ∏è Supprimer</button>
                  </>
                )}
                {userRole !== 'admin' && <em>Lecture seule</em>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <nav>
        <ul className="pagination justify-content-center">
          {[...Array(Math.ceil(filteredCommandes.length / itemsPerPage)).keys()].map(number => (
            <li key={number + 1} className={`page-item ${currentPage === number + 1 ? 'active' : ''}`}>
              <button className="page-link" onClick={() => setCurrentPage(number + 1)}>{number + 1}</button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default App;
